mapper.select:
SELECT p.cuscode, c.cCusName AS cusName, p.workshopcode, ws.workshopname, p.workstationcode, p.invcode, i.cInvName AS invName, p.planqty
FROM prodplan p
INNER JOIN Customer c ON p.cuscode = c.cCusCode
INNER JOIN workshop ws ON p.workshopcode = ws.workshopcode
INNER JOIN Inventory i ON p.invcode = i.cInvCode
LEFT JOIN work w ON p.id = w.prodPlanId


        AND p.workShopCode = #{planProcessSearchDTO.uworkshop}


        AND p.cusCode = #{planProcessSearchDTO.ucuscode}


        AND p.invCode = #{planProcessSearchDTO.uinvcode}

    AND p.plandate = CONVERT(varchar(10),GETDATE(),120)
