mapper.select:
SELECT workStationCode
FROM workstation
WHERE ipaddress = #{ipAddress, jdbcType=NVARCHAR}

SELECT startTime
FROM work
WHERE prodPlanId = #{prodPlanId}

mapper.update:
UPDATE work
SET startTime = getdate()
WHERE prodPlanId = #{prodPlanId}

UPDATE work
SET qualifiedqty = isnull(qualifiedqty,0) + 1
WHERE prodPlanId = #{prodPlanId}

UPDATE work
SET unqualifiedqty = isnull(unqualifiedqty,0) + 1
WHERE prodPlanId = #{prodPlanId}
